tinymce.PluginManager.add('aspimage', function(editor, url) {
	function showDialog() {
        var url = "http://example.com/popup.php";
        var frame = editor.windowManager.open({
                title: 'Insert/edit image',
                url: 'js/tinymce/plugins/aspimage/aspimage.asp',
                width: 480,
                height: 385,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    //editor.insertContent('Title: ' + e.data.title);
                }
            });
		window.my_namespace_activeEditor = editor;
        window.my_namespace_activeFrame = frame;
	}

    // Add a button that opens a window
    editor.addButton('aspimage', {
        image: "js/tinymce/plugins/aspimage/img/sample.gif",
        icon: true,
		tooltip: "Insert/edit image",
        onclick: showDialog
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('aspimage', {
        image: "js/tinymce/plugins/aspimage/img/sample.gif",
        icon: true,
        text: 'Insert image',
        context: 'insert',
        onclick: showDialog
    });
});